    self.driver.find_element(By.ID, "id_escola-areacientifica").click()
    self.driver.find_element(By.ID, "id_escola-areacientifica").send_keys("Ciências e Tecnologia")
    element = self.driver.find_element(By.CSS_SELECTOR, ".b-numberinput .input")
