    value = self.driver.find_element(By.ID, "id_escola-nome_escola").get_attribute("value")
    assert value == "Judice Fialho"
    value = self.driver.find_element(By.ID, "id_escola-areacientifica").get_attribute("value")
    assert value == "Ciências e Tecnologia"
    self.driver.find_element(By.CSS_SELECTOR, ".is-success > span:nth-child(1)").click()
  
