class TestLogin():
  def setup_method(self, method):
    self.driver = webdriver.Firefox()
    self.vars = {}
  
  def teardown_method(self, method):
    self.driver.quit()
  
  def test_login(self):
    self.driver.get("http://localhost:8000/")
    self.driver.set_window_size(1920, 1080)
    self.driver.find_element(By.CSS_SELECTOR, "a:nth-child(2) > .button").click()
    self.driver.find_element(By.ID, "id_username").click()
    self.driver.find_element(By.ID, "id_username").send_keys("tiago")
    self.driver.find_element(By.ID, "id_password").send_keys("andre123456")
    self.driver.find_element(By.ID, "id_password").send_keys(Keys.ENTER)
    assert self.driver.find_element(By.CSS_SELECTOR, ".message-body strong").text == "Bem vindo(a) Tiago"
  
